<?php$serverlist_file = fopen('http://ace-spades.com/serverlist.txt', "r");$continue = true;$serverlist = '';while($continue) {	if(($buffer = fgets($serverlist_file, 1024)) !== false)	{		$serverlist .= '---|||---' . $buffer;	}	else		$continue = false;}fclose($serverlist_file);$serverlist = substr($serverlist, 9);$serverlist = explode('---|||---', $serverlist);$listservers = '';foreach($serverlist as $server){	$joueursco = preg_replace('#(.+)/\s?(.+) (.+) <img src="/wp-content/uploads/flags/_(.+)\.png" border="0" /> <a href="aos://(.+)">(.+)?</a>#', '$1', $server);	$joueurstot = preg_replace('#(.+)/\s?(.+) (.+) <img src="/wp-content/uploads/flags/_(.+)\.png" border="0" /> <a href="aos://(.+)">(.+)?</a>#', '$2', $server);	$ping = preg_replace('#(.+)/\s?(.+) (.+) <img src="/wp-content/uploads/flags/_(.+)\.png" border="0" /> <a href="aos://(.+)">(.+)?</a>#', '$3', $server);	$pays = preg_replace('#(.+)/\s?(.+) (.+) <img src="/wp-content/uploads/flags/_(.+)\.png" border="0" /> <a href="aos://(.+)">(.+)?</a>#', '$4', $server);	$id = preg_replace('#(.+)/\s?(.+) (.+) <img src="/wp-content/uploads/flags/_(.+)\.png" border="0" /> <a href="aos://(.+)">(.+)?</a>#', '$5', $server);	$nom = preg_replace('#(.+)/\s?(.+) (.+) <img src="/wp-content/uploads/flags/_(.+)\.png" border="0" /> <a href="aos://(.+)">(.+)?</a>#', '$6', $server);	$listservers .= trim('---|||---' . trim($joueursco) . '-|-' . trim($joueurstot) . '-|-' . trim($ping) . '-|-' . trim($pays) . '-|-' . trim($id) . '-|-' . $nom, "\n");}$listservers = substr($listservers, 9);echo $listservers;